function generateMarkovChain(text, order = 2) {
  const words = text.split(' ');
  const chain = {};

  // Build the chain
  for (let i = 0; i < words.length - order; i++) {
    const key = words.slice(i, i + order).join(' ');
    const nextWord = words[i + order];

    if (!chain[key]) chain[key] = [];
    chain[key].push(nextWord);
  }

  return chain;
}

// Generate text using the Markov chain
function generateText(chain, startWord, length = 20) {
  const result = [startWord];
  let currentKey = startWord;

  for (let i = 0; i < length - 1; i++) {
    const nextWords = chain[currentKey];
    if (!nextWords || nextWords.length === 0) break;

    const nextWord = nextWords[Math.floor(Math.random() * nextWords.length)];
    result.push(nextWord);
    currentKey = result.slice(-order).join(' ');
  }

  return result.join(' ');
}

